import React from "react";
import ".style.css";
import mytext from "./text.js";

class Hackertype extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      text: [],
      timerKey: 0,
      textStart: null
    };
    this.setTextStartRef = element => {
      this.textStart = element;
    };
  }
  clickOn(e) {
    const addText = [
      mytext[this.state.timerKey],
      mytext[this.state.timerKey + 1],
      mytext[this.state.timerKey + 2]
    ];
    this.setState(state => ({
      timerKey: state.timerKey + 3,
      text: [...state.text, addText]
    }));
  }
  componentDidMount() {
    this.addText.focus();
  }
  render() {
    return (
      <div
        ref={this.setTextStartRef}
        className="hackerType"
        onClick={e => this.clickOn(e)}
        tabIndex="0"
      >
        {this.state.text}
      </div>
    );
  }
}

export default Hackertype;
